import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useParams } from 'react-router-dom';
import ReactStars from 'react-rating-stars-component';
import { useSelector } from 'react-redux'

const ProductDetails = () => {
  const [user_info, setUser_info] = useState([])
  const [product, setProduct] = useState([])
  const [getComment, setgetComment] = useState([])
  const { id } = useParams();
  const { user } = useSelector((state) => state.user)
  const user_id = user.id
  console.log(id);

  useEffect(() => {
    getProduct();
  }, [])


  const [comment, setComment] = useState('')
  const [review, setReview] = useState(0)

  const ratingChanged = (review) => {
    setReview(review)
  };

  const getProduct = () => {
    Axios.get(`/products/${id}`,)
      .then((res) => {
        setProduct(res.data.data.product)

      })
      .catch(err => { throw err });
  }

  const reviews = () => {
    Axios.post('/set-review', {
      user_id,
      id,
      review,
      comment
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  return (
    <>
      <Card style={{ alignItems: 'center', padding: '50px' }}>

        <Card.Title><h1>{product.product}</h1></Card.Title>
        <Card.Img style={{ width: '500px', height: '500px' }} variant="top" src={'http://localhost:5000/images/' + product.img} />
        <Card.Body>
          <Card.Subtitle>{product.description}</Card.Subtitle>
        </Card.Body>
        <div className="input01" style={{ align: 'center' }}>
          <ReactStars
            count={5}
            onChange={ratingChanged}
            size={30}
            activeColor="#78c2ad" />
          <input name='comment' className='input0' type='text' placeholder='Review' onChange={(e) => setComment(e.target.value)} /><br />
        </div>
        <Button type='button' variant='outline-secondary' onClick={reviews}>Submit</Button>
      </Card>


        <Card>
          {product.reviews.map((review) => {
            return (
              <Card key={review.id} style={{ padding: '50px', align: 'center' }}>
                <Card.Body>
                  <Card.Text>
                    user id: {review.user_id} {review.name} {review.surname}<br />
                    review: {review.review} <br />
                  </Card.Text>
                  <Card.Text>comment: <br />
                    {review.comment}
                  </Card.Text>
                </Card.Body>
              </Card>
            )
          })}
        </Card>
    </ >
  )
}

export default ProductDetails
