import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";

const AddProduct = () => {

    const [file, setFile] = useState("");
    const [fileName, setFileName] = useState("");

    const saveFile = (e) => {
        setFile(e.target.files[0]);
        setFileName(e.target.files[0].name);
    };
    const uploadFile = async (e) => {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("fileName", fileName);

    };


    const [product, setProduct] = useState('')
    const [description, setDescription] = useState('')
    const [img, setImg] = useState('')

    const products = () => {
        Axios.post('/add-product', {
            product,
            description,
            img
        })
            .then(response => {
                console.log(response)
            })
            .catch(err => { throw err });
    };


    return (
        <Header title='Add new product'>
            <Card className="text-center">
                <Card.Header>
                    <h2 style={{ textAlign: "left" }}>Product details</h2>
                </Card.Header>
                <Card.Body>

                    <Card.Text >
                        <form >
                            <input name='product' className='input0' type='text' placeholder='Products name' required onChange={(e) => setProduct(e.target.value)} />
                            <br /><br />


                            <textarea name='description' className='input' type='text' placeholder='Description' required onChange={(e) => setDescription(e.target.value)} />

                            <br />

                            <input name='img' type="file" onChange={saveFile} />

                        </form>
                    </Card.Text>
                    <Button type="button" onClick={products}>Add</Button>
                </Card.Body>
            </Card>
        </Header>




    )
}
export default AddProduct
