import { Dropdown, Navbar, Nav, Form, Container, FormControl, Button, DropdownButton } from 'react-bootstrap';
import './bootstrap.min.css';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';

import React from 'react'

const Topbar = () => {
  const { user } = useSelector((state) => state.user)
  const navigate = useNavigate()

  const logo = () => {
    localStorage.key.length > 0
      ? navigate('/products')
      : navigate('/login')
  }
  const logout = () => {
    localStorage.key.removeItem('persist:root')
    navigate('/')
  }
  return (
    <Navbar bg="light" expand="lg">
      <Container fluid>
        <Navbar.Brand onClick={logo} style={{ fontWeight: 'bold', fontSize: 25, padding: 10, cursor: "pointer" }} >
          <Img src="/docs/4.0/assets/brand/bootstrap-solid.svg" width="30" height="30" alt=""/>
  </Navbar.Brand>
        <Nav
          className="me-auto my-2 my-lg-0"
          style={{ maxHeight: '100px', maxWidth: '100%' }}>

          {
            user.isAdmin === true

              ? <>
                <DropdownButton variant="success" id="dropdown-basic" title="Admin" >
                  <Dropdown.Menu>
                    <Dropdown.Item href="/users" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>Users</Dropdown.Item>
                    <Dropdown.Item href="/add-product" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>New Product</Dropdown.Item>
                  </Dropdown.Menu>
                </DropdownButton>
                <Link to="/products" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>Products</Link><br />
              </>

              : <><Link to="/products" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>Products</Link><br /></>
          }
          <Nav>
            <Form className="d-flex">
              <FormControl
                type="search"
                placeholder="Search"
                aria-label="Search"
              />

            </Form>
          </Nav>
        </Nav>
        <Nav><Button style={{ fontWeight: 'bold', fontSize: 20, padding: 20 }} type="button" onClick={logout} >Logout</Button></Nav>


      </Container>
    </Navbar>
  )
}

export default Topbar
