import ReactStars from "react-rating-stars-component";
import React, { useEffect, useState } from 'react'
import { Button, Card, CardGroup, Col, NavLink, Row } from 'react-bootstrap';
import Header from './Header';
import './Products.css';

import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useSelector } from 'react-redux';

const Products = () => {
  const { user } = useSelector((state) => state.user)
  const [comment, setComment] = useState('')
  const [review, setReview] = useState(0)
  const [products, setProducts] = useState([])

  const ratingChanged = (review) => {
    setReview(review)
  };

  const reviews = () => {
    Axios.post('/set-review', {
      review,
      comment
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  useEffect(() => {
    Axios.get('/products')
      .then((res) => {
        setProducts(res.data.data.products)
      }); 
  }, [])

  return (
    <Header title='Products'>
      <CardGroup style={{ display: 'flex', flexDirection: 'row' }}>
      <Row >
        {products.map((product) => {

          return (
            <Col sm={4}>
            <Card key={product.prodid} style={{ width: '18rem', padding:'20px' }}>
              <Card.Img style={{width:'18rem' ,height: '18rem'}} variant="top" src={'http://localhost:5000/images/' + product.img } />
              <Card.Body>
                <Card.Title>{product.product}</Card.Title>
                <Card.Text>
                  {product.description}
                </Card.Text>

                <NavLink to={`/products/${product.prodid}`}>
                  <Button variant="primary">View</Button>
                </NavLink>

              </Card.Body>
            </Card>
            </Col>
          )


        })}

      </Row>
      </CardGroup>
      {/* <div className="input01">

        <ReactStars
          count={5}
          onChange={ratingChanged}
          size={30}
          activeColor="#2045B6" />
        <input name='comment' className='input0' type='text' placeholder='Review' onChange={(e) => setComment(e.target.value)} /><br />

        <Button type='button' onClick={reviews}>Submit</Button> </div> */}


    </Header>



  );
}

export default Products
