import ReactStars from "react-rating-stars-component";
import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";

const Products = () => {

  const [comment, setComment] = useState('')
  const [review, setReview] = useState(0)

  const [productList, setProductList] = useState([]);
  useEffect(()=>{

    const getProductList=async()

  },[]);




 const ratingChanged = (review) => {
   const a=review;
   return(console.log(a));

  };

  const Products = () => {
    Axios.get('/Products')
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  const reviews = (review) => {
    Axios.post('/Products',{
      review,
      comment
  })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  return (
    <Header title='Products'>
      <Card className="text-center">
        <Card.Header><h1>Name</h1></Card.Header>
        <Card.Body>
          <Card.Text style={{ marginBottom: 20 }}>
           {/* <Card.Img variant="bottom" src="holder.js/100px180" /> */}
          </Card.Text>
          <Button variant="primary" >Prev</Button>
          <Button variant="primary" >Update</Button>
          <Button variant="primary" >Delete</Button>
          <Button variant="primary" >Next</Button>
        </Card.Body>

      </Card>

      <div className="input01">

        <ReactStars
        count={5}
        onChange={ratingChanged}
        size={30}
        activeColor="#2045B6" />
        <input name='comment' className='input0' type='text' placeholder='Review' onChange={(e) => setComment(e.target.value)} /><br />

        <Button type='button' onClick={reviews}>Submit</Button> </div>


    </Header>



  );
}

export default Products
