import { Button, Card, Col } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useNavigate, useParams, Link } from 'react-router-dom';

const ProductDetails = () => {
  const[product, setProduct]=useState([])
  const {id} = useParams();
  console.log(id);
useEffect(() => {
 Axios.get(`/products/id=/${id}`).then((res) => {
  setProducts(res.data.data.product);

});
}, [])


  return (
    <Header title='Details'>
      <Card>
      {product.map((prod) => {

return (
  <Col key={prod.prodid} style={{ padding: '20px' }}>
    <Card style={{ width: '18rem' }}>
      <Card.Img style={{ width: '18rem', height: '18rem' }} variant="top" src={'http://localhost:5000/images/' + prod.img} />
      <Card.Body>
        <Card.Title>{prod.product}</Card.Title>
          </Card.Body>
    </Card>
  </Col>
)


})}



      </Card>
      <Link to={`/products`}>
        <Button variant="primary">Back</Button>
      </Link  >
    </Header >
  )
}

export default ProductDetails
