import ReactStars from "react-rating-stars-component";
import React, { useEffect, useState } from 'react'
import { Button, Card, Container, Form, FormControl, FormGroup, FormLabel, Row } from 'react-bootstrap';
import Header from './Header';
import './Products.css';

import './components/Topbar/bootstrap.min.css';
import Axios from "axios";

const Products = () => {

  const [comment, setComment] = useState('')
  const [review, setReview] = useState(0)

  const ratingChanged = (review) => {
    setReview(review)
  };

  // const Products = () => {
  //   Axios.get('/Products')
  //     .then(response => {
  //       console.log(response)
  //     })
  //     .catch(err => { throw err });
  // };

  const reviews = () => {
    Axios.post('/Products', {
      review,
      comment
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  useEffect(() => {
    Axios.get('/Products')
      .then((res) => {
        return (res)
      });
  }, [])




  return (
    <Header title='Products'>
      <table>
        <Card className="text-center">
          <td><Card.Header><h1>Name</h1></Card.Header></td>
          <th>
          <tbody>
        {this.state.availabilities.map(availability => {
          return (
            <tr>
              <td>{availability.id}</td>
            </tr>
          );
        })}
      </tbody>
          </th>
        </Card>
      </table>
      <div className="input01">

        <ReactStars
          count={5}
          onChange={ratingChanged}
          size={30}
          activeColor="#2045B6" />
        <input name='comment' className='input0' type='text' placeholder='Review' onChange={(e) => setComment(e.target.value)} /><br />

        <Button type='button' onClick={reviews}>Submit</Button> </div>


    </Header>



  );
}

export default Products
