import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

const ProductDetails = () => {
  const [products, setProducts] = useState([])
  const navigate = useNavigate()
  const id = useParams()

  const back = () => {
    navigate('/products')
  }
  useEffect(() => {
    Axios.get('/products/id=' + id)
      .then((res) => {
        setProducts(res.data.data.products)
      });
  }, [])
  return (
    <Header title=''>
      {products.map((product) => {
        console.log(id, "sefd");
        return (
          <Card key={product.prodid} style={{ width: '18rem' }}>
            <Card.Img style={{ width: '18rem', height: '18rem' }} variant="top" src={'http://localhost:5000/images/' + product.img} />
            <Card.Body>
              <Card.Title>{product.product}</Card.Title>
              <Card.Text>
                {product.description}
              </Card.Text>
              <Button type="button" onClick={back}>Back to products page</Button>
            </Card.Body>
          </Card>, console.log(id)
        )
      })}



    </Header >
  )
}

export default ProductDetails
