import React, { useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom';
import "./components/Topbar/bootstrap.min.css";
import { Button, Form } from 'react-bootstrap'
export default function Register() {
  const [name, setNewName] = useState('');
  const [surname, setSurname] = useState('');
  const [age, setNewage] = useState('');
  const [email, setNewuser] = useState('');
  const [password, setNewpass] = useState('');
  const [error, setError] = useState('')
  const navigate = useNavigate()

  const register = () => {
    axios.post('/register', {
      name,
      surname,
      age,
      email,
      password,
    })
      .then(response => {
        navigate('/login')
        console.log(response)
      })
      .catch(err => {
        const response = err.response.data.validationError[0];
        if (response) {
          setError(`${response.field}: ${response.message}`)
        }
      });
  };


  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '80vh' }}>


      <Form >
        <div className="form-group" style={{ border: "1px solid #78c2ad", padding: "70px" }}>
          <h3>Name:</h3>
          <input type="text" onChange={(e) => { setNewName(e.target.value) }} name="name" />

          <h3>Surname:</h3>
          <input type="text" onChange={(e) => { setSurname(e.target.value) }} name="surname" />

          <h3>Age:</h3>
          <input type="date" onChange={(e) => { setNewage(e.target.value) }} name="age" />

          <h3>Email:</h3>
          <input type="email" onChange={(e) => { setNewuser(e.target.value) }} name="email" />

          <h3>Password:</h3>
          <input type="password" onChange={(e) => { setNewpass(e.target.value) }} name="password" required />

          <div >
          <Button onClick={register} variant="outline-secondary">Register</Button>
          </div>
          <div className='text-center font-weight-bold text-danger'>
            {error}
          </div>
          <a href='/login'>Login</a>
          </div>
      </Form>


    </div>
  )
}
