import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useNavigate, useParams, Link } from 'react-router-dom';

const ProductDetails = () => {
  const[product, setProduct]=useState([])
  const {id} = useParams();
  console.log(id);
useEffect(() => {
 Axios.get(`/products/id=/${id}`).then((res) => {
  setProduct(res.data.data.product);

});
}, [])


  return (
    <Header title='Details'>

{product.map((products) => {

            return (

                <Card key={products.prodid} style={{ width: '18rem' }}>
                  <Card.Img style={{ width: '18rem', height: '18rem' }} variant="top" src={'http://localhost:5000/images/' + products.img} />
                  <Card.Body>
                    <Card.Title>{products.product}</Card.Title>

                  </Card.Body>
                </Card>

            )


          })}

      <Link to={`/products`}>
        <Button variant="primary">Back</Button>
      </Link  >
    </Header >
  )
}

export default ProductDetails
