import { Dropdown, Navbar, Nav, Form, Container, FormControl, Button, DropdownButton } from 'react-bootstrap';
import './bootstrap.min.css';
import { Link, useNavigate } from 'react-router-dom';


import { useDispatch, useSelector } from 'react-redux'
import { setUser } from '/home/user/test-project/client/src/UserStore.js'

//button - f6b5b8
import React from 'react'

const Topbar = () => {
  const { user } = useSelector((state) => state.user)
  const dispatch = useDispatch()
  const navigate = useNavigate()

  const logo = () => {

    if (Object.keys(user).length > 0) {
      navigate('/products')
    }
    else { navigate('/') }
  }

  const logout = () => {

    if (Object.keys(user).length > 0) {
      dispatch(setUser([]));
      navigate('/')
    }
    else <></>
  }
  return (
    <Navbar bg="light" expand="lg">
      <Container fluid>
        <Navbar.Brand onClick={logo} style={{ fontWeight: 'bold', fontSize: 25, padding: 10, cursor: "pointer" }} >
