        </Navbar.Brand>
        <Nav
          className="me-auto my-2 my-lg-0"
          style={{ maxHeight: '100px', maxWidth: '100%' }}>
          {
            Object.keys(user).length > 0
              ? <>
                {
                  !!user.isAdmin
                    ? <>
                      <DropdownButton variant="secondary" id="dropdown-basic" title="Admin" >

                          <Dropdown.Item>
                            <Link to="/users" style={{  padding: 10 }}>Users</Link>
                          </Dropdown.Item>

                          <Dropdown.Item>

                         < Link to="/users" style={{  padding: 10 }}>Reviews</Link>
                          </Dropdown.Item>
                          <Dropdown.Item>
                            <Link to="/add-product" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>New Product</Link>
                          </Dropdown.Item>

                      </DropdownButton>
                      <Link to="/products" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }} >Products</Link>
                    </>

                    : <Link to="/products" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>Products</Link>

                }
                   <Nav>

              <FormControl
                type="search"
                placeholder="Search"
                aria-label="Search"
              />


          </Nav>
              </>


              : <></>
          }


        </Nav>
        {Object.keys(user).length > 0
        ? <Nav><Button style={{ fontWeight: 'bold', fontSize: 20 }}  class="btn btn-primary btn-sm" onClick={logout} >Logout</Button></Nav>
        :<></>}


      </Container>
    </Navbar>
  )
}

export default Topbar
