import { createStore } from "redux";

const store= createStore(function(state,action){
if (action.type==="edirCurrentUser"){
    return{
        ...state,
        currentUser:{
            name:action.payload.name
        }
    }
}
return state;

}, {
    currentUser:{
        name:"edwe"
    }
});


export default store
