import ReactStars from "react-rating-stars-component";

import React, { useEffect, useState } from 'react'
import { Button, Card, CardGroup, Col, Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Header from './Header';
import './Products.css';
import ProductDetails from './ProductDetails';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useSelector } from 'react-redux';

const Products = () => {
  const { user } = useSelector((state) => state.user)
  console.log(user.id);
  const [products, setProducts] = useState([])

  useEffect(() => {
    Axios.get('/products')
      .then((res) => {
        setProducts(res.data.data.products)
      });
  }, [])

  return (
    <Header title='Products'>
      <CardGroup style={{ display: 'flex', flexDirection: 'row' }}>
        <Row >
          {products.map((product) => {

            return (
              <Col key={product.prodid} style={{ padding: '20px' }}>
                <Card style={{ width: '18rem' }}>
                  <Card.Img style={{ width: '18rem', height: '18rem' }} variant="top" src={'http://localhost:5000/images/' + product.img} />
                  <Card.Body>
                    <Card.Title>{product.product}</Card.Title>
                    <Link to={`/products/${product.prodid}`  }>
                      <Button variant="primary" >View</Button>
                    </Link  >
                  </Card.Body>
                </Card>
              </Col>
            )


          })}

        </Row>
      </CardGroup>
      {/* */}


    </Header>



  );
}

export default Products
