import { Dropdown, Navbar, Nav, Form, Container, FormControl, Button, DropdownButton, NavLink } from 'react-bootstrap';
import './bootstrap.min.css';
import { Link } from 'react-router-dom';


import { useDispatch, useSelector } from 'react-redux'
import { setUser } from '/home/user/test-project/client/src/UserStore.js'


import React from 'react'

const Topbar = () => {
  const { user } = useSelector((state) => state.user)
  const dispatch = useDispatch()
  const navigate = useNavigate()

  const logo = () => {

    if (user && Object.keys(user).length > 0) { navigate('/products') }
    else { navigate('/') }
  }

  const logout = () => {

    if (user && Object.keys(user).length > 0) {
      dispatch(setUser([]));
      navigate('/')
    }
    else <></>
  }
  return (
    <Navbar bg="light" expand="lg">
      <Container fluid>
        <Navbar.Brand onClick={logo} style={{ fontWeight: 'bold', fontSize: 25, padding: 10, cursor: "pointer" }} >
          LOGO
        </Navbar.Brand>
        <Nav
          className="me-auto my-2 my-lg-0"
          style={{ maxHeight: '100px', maxWidth: '100%' }}>

          {
            user && Object.keys(user).length > 0
            ? <>
                {
                  !!user.isAdmin
                       ? <>            
                      <DropdownButton variant="success" id="dropdown-basic" title="Admin" >
                        <Dropdown.Menu>
                          <Dropdown.Item>
                            <Link to="/users" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>Users</Link>
                          </Dropdown.Item>
                          <Dropdown.Item>
                            <Link to="/add-product" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>New Product</Link>
                          </Dropdown.Item>
                        </Dropdown.Menu>
                      </DropdownButton>
                      <Link to="/products" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>Products</Link>
                        </>



            :<>


            </>}
            </>


            : <>{console.log(!!user)}</>
          }

          <Nav>
            <Form className="d-flex">
              <FormControl
                type="search"
                placeholder="Search"
                aria-label="Search"
              />

            </Form>
          </Nav>  
        </Nav>
        <Nav><Button style={{ fontWeight: 'bold', fontSize: 20, padding: 20 }} type="button" onClick={logout} >Logout</Button></Nav>


      </Container>
    </Navbar>
  )
}

export default Topbar
