import Axios from 'axios';
import React, { useState } from 'react'
import Header from './Header';
import "./components/Topbar/bootstrap.min.css";
import {  useDispatch } from 'react-redux'
import { setUser  } from './UserStore'
import { useNavigate } from 'react-router-dom';
import { Button, Form, link } from 'react-bootstrap';

export default function Login() {
  const dispatch = useDispatch()
  const [email, setEmail] = useState('');
  const [password, setPass] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();  

  const login = () => {

    Axios.post('/login', {
      email,
      password,
    })
      .then(response => {
        navigate('/products');
        dispatch(setUser(response.data.data.user))          
      })
      .catch(err => {
        console.log(err);
        const response = err.response.data.validationError[0];
        if (response) {
          setError(`${response.message}`)
        }
      });
  };
  return (

      <Form>
        <div class="form-group">
          <div className="inputs">
             <div className='text-left font-weight-bold text-danger'>
              {error}
            </div>
            <h3>Username:</h3>
            <input type="email"
              onChange={(e) => {
                 setEmail(e.target.value);
              }}
              name="email" />

            <h3>Password:</h3>
            <input type="password" onChange={(e) => { setPass(e.target.value) }} name="password" />
            <Button variant='outline-secondary'onClick={() => login()} >Login</Button>
            <br /> <br />
            <Lnik to="/Register"> Register </Lnik>
          </div>
        </div></Form>
  );
}
