import React, { useEffect, useState } from 'react'
import Header from './Header'
import { Pagination, Table } from 'react-bootstrap';
import Axios from 'axios';
import ReactPaginate from 'react-paginate';

export default function Users() {
  const [users, setUser] = useState([]);
  const [pageNumber, setPageNumber] = useState(0)
  const usersPerPage = 10
  const visitedPages = pageNumber * usersPerPage


  const displayUser = users.slice(visitedPages, visitedPages + usersPerPage).map((user) => {
    return (
      <tr key={user.id} >
        <td>{user.id}</td>
        <td>{user.name}</td>
        <td>{user.surname}</td>
        <td>{new Date().getFullYear() - new Date(user.age).getFullYear()}</td>
        <td>{user.email}</td>
        <td>{user.isAdmin}</td>
      </tr>)

  })
  useEffect(() => {
    Axios.get('/users')
      .then((response) => {
        setUser(response.data.data.users)
      });
  }, [])

  const pageCount = Math.ceil(users.length / usersPerPage)
  const pageChange = ({ selected }) => {
    setPageNumber(selected)
  }
  return (
    <Header title='Users list'>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>First Name</th>
            <th>Surname</th>
            <th>Age</th>
            <th>email</th>
            <th>isAdmin</th>
          </tr>
        </thead>
        <tbody>
          {displayUser}
        </tbody>
      </Table>
      <ReactPaginate style={{display:"inline-block", listStyle:""}}
        pageCount={pageCount}
        onPageChange={pageChange} />
    </Header>
  )
}
