import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useNavigate, useParams, Link } from 'react-router-dom';

const ProductDetails = () => {
  const[products, setProducts]=useState([])
  const {id} = useParams();
  console.log(id);
useEffect(() => {
 Axios.get(`/products/id=/${id}`).then((res) => {
  setProducts(res.data.data.products);

});
}, [])


  return (
    <Header title='Details'>
      <Card>
{products.map((product) => {

            return (

                <Card key={product.prodid} style={{ width: '18rem' }}>
                  <Card.Img style={{ width: '18rem', height: '18rem' }} variant="top" src={'http://localhost:5000/images/' + product.img} />
                  <Card.Body>
                    <Card.Title>{product.product}</Card.Title>
                    <Link to={`/products/id=/${product.prodid}`  }>
                      <Button variant="primary" >View</Button>
                    </Link  >
                  </Card.Body>
                </Card>

            )


          })}



      </Card>
      <Link to={`/products`}>
        <Button variant="primary">Back</Button>
      </Link  >
    </Header >
  )
}

export default ProductDetails
