import Axios from 'axios';
import React, { useState } from 'react'
import Header from './Header';
import "./Register.css";
import { useDispatch, useSelector } from 'react-redux';

export default function Login() {

  const [email, setUser] = useState('');
  const [password, setPass] = useState('');
  const [error, setError] = useState('');

  const name = useSelector(function (state) {
    return state.currentUser.name;
  });
  const dispatch = useDispatch();



  const login = () => {

    Axios.post('/login', {
      email,
      password,
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => {
        const response = err.response.data.validationError[0];
        if (response) {
          setError(`${response.message}`)
        }
      });
  };
  return (

    <Header title='Login'> <h1>{name}</h1>
      <form className="register-form">
        <div className="home">
          <div className="inputs">
            <h3>Username:</h3>
            <input type="email"
              onChange={(e) => {
                setUser(e.target.value);

              }}
               name="email" value={name} />
            <div className='text-center font-weight-bold text-danger'>
              {error}
            </div>
            <h3>Password:</h3>
            <input type="password" onChange={(e) => { setPass(e.target.value) }} name="password" />
            <button type='button' onClick={login} className="button">Login</button>
            <br /> <br />
            <a href="/Register"> Register </a>
          </div>
        </div></form></Header>
  );
}
