import Axios from 'axios';
import React, { useState } from 'react'
import Header from './Header';
import "./Register.css";
import {  useDispatch } from 'react-redux'
import { setUser  } from './UserStore'
import { Navigate } from 'react-router-dom';

export default function Login() {
  const dispatch = useDispatch()
  const [email, setEmail] = useState('');
  const [password, setPass] = useState('');
  const [error, setError] = useState('');


  const login = () => {

    Axios.post('/login', {
      email,
      password,
    })
      .then(response => {
        dispatch(setUser(response.data.data.user))
        navigate('/products');
      })
      .catch(err => {
        console.log(err);
        const response = err.response.data.validationError[0];
        if (response) {
          setError(`${response.message}`)
        }
      });
  };
  return (

    <Header title='Login'> 
      <form className="register-form">
        <div className="home">
          <div className="inputs">
             <div className='text-left font-weight-bold text-danger'>
              {error}
            </div>
            <h3>Username:</h3>
            <input type="email"
              onChange={(e) => {
                 setEmail(e.target.value);
              }}
              name="email" />

            <h3>Password:</h3>
            <input type="password" onChange={(e) => { setPass(e.target.value) }} name="password" />
            <button type='button' onClick={() => login()} className="button">Login</button>
            <br /> <br />
            <a href="/Register"> Register </a>
          </div>
        </div></form></Header>
  );
}
