import { Navbar, Nav, Form, Container, FormControl, Button } from 'react-bootstrap';
import './bootstrap.min.css';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';

import React from 'react'

const Topbar = () => {
  const { user } = useSelector((state) => state.user)
  const navigate = useNavigate()

  const logo = () => {
    localStorage.getItem('persist:root').user > 0
      ? navigate('/products')
      : navigate('/')
  }
  const logout = () => {
    delete localStorage.test
    navigate('/')
  }
  return (
    <Navbar bg="light" expand="lg">
      <Container fluid>
        <Button type='button' onClick={logo} style={{ fontWeight: 'bold', fontSize: 25, padding: 10 }}>LOGO</Button>
        <Nav
          className="me-auto my-2 my-lg-0"
          style={{ maxHeight: '100px' }}>

          {user.isAdmin === true
            ? <> <Link to="/users" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>Users</Link> <br />
              <Link to="/add-product" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>New Product</Link><br />
              <Link to="/products" style={{ fontWeight: 'bold', fontSize: 20, padding: 10 }}>Products</Link><br /></>
            : <></>}
          <Nav>
            <Form className="d-flex">
              <FormControl
                type="search"
                placeholder="Search"
                aria-label="Search"
              />

            </Form>
          </Nav>
        </Nav>
        <Nav><Button style={{ fontWeight: 'bold', fontSize: 20, padding: 20 }} type="button" onClick={logout} >Logout</Button></Nav>


      </Container>
    </Navbar>
  )
}

export default Topbar
