import ReactStars from "react-rating-stars-component";
import React, { useEffect, useState } from 'react'
import { Button, Card, Container, Form, FormControl, FormGroup, FormLabel, Row } from 'react-bootstrap';
import Header from './Header';
import './Products.css';

import './components/Topbar/bootstrap.min.css';
import Axios from "axios";

const Products = () => {

  const [comment, setComment] = useState('')
  const [review, setReview] = useState(0)
  const [products, setProductList] = useState([])

  const ratingChanged = (review) => {
    setReview(review)
  };

  const reviews = () => {
    Axios.post('/set-review', {
      review,
      comment
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  useEffect(() => {
    Axios.get('/products')
      .then((res) => {
        setProductList(res.data.data.products)
      });
  }, [])

  return (
    <Header title='Products'>
      {products.map((product) => {
        return (
          <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" >{product.img}</Card.Img>
            <Card.Body>
              <Card.Title>{product.product} </Card.Title>
              <Card.Text>
                {product.description}
              </Card.Text>
              <Button variant="primary">View</Button>
            </Card.Body>
          </Card>)



      })}

