import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useNavigate } from 'react-router-dom';

const ProductDetails = () => {
    const [products, setProducts] = useState([])
    const navigate = useNavigate();
    const back=()=>
        {
            navigate('/products')
        }
        useEffect(() => {
            Axios.get(`/products/${products.prodid}`)
              .then((res) => {
                setProducts(res.data.data.products)
              }); 
          }, [])
    return (
        <Header title='Products'>
        <CardGroup style={{ display: 'flex', flexDirection: 'row' }}>
        <Row >
          {products.map((product) => {

            return (
              <Col key={product.prodid} md={3} style={{ padding:'20px'}}>
              <Card style={{ width: '18rem' }}>
                <Card.Img style={{width:'18rem' ,height: '18rem'}} variant="top" src={'http://localhost:5000/images/' + product.img } />
                <Card.Body>
                  <Card.Title>{product.product}</Card.Title>
                  <Card.Text>
                    {product.description}
                  </Card.Text>

                  <NavLink to={`/products/id=${product.prodid}`}>
                    <Button variant="primary">View</Button>
                  </NavLink>

                </Card.Body>
              </Card>
              </Col>
            )


          })}

        </Row>
        </CardGroup>
        </Header >
    )
}

export default ProductDetails
