import React from 'react'
import { Button, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Header from './Header'

const ChangePassword = () => {
    return (
    <Header title="Change your password">
         <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
      <Form>
        <div className="form-group" style={{ border: "1px solid #78c2ad", padding: "20px" }}>
          <div className="inputs">
            <div className='text-left font-weight-bold text-danger'>
              {error}
            </div>
            <h3>Username:</h3>
            <input type="email"
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              name="email" /><br />
            <h3>Password:</h3>
            <input type="password" onChange={(e) => { setPass(e.target.value) }} name="password" /><br /><br />

            <Button variant='outline-secondary' onClick={() => login()} >Login</Button>
            <br /> <br />
            <Link to="/Register"> Register </Link>
            <Link to="/change-password"> Forgot Password  </Link>
          </div>
        </div>
      </Form>
    </div>
    </Header>
    )
}

export default ChangePassword
