import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useParams } from 'react-router-dom';
import ReactStars from 'react-rating-stars-component';
import { useSelector } from 'react-redux'

const ProductDetails = () => {
  const [user_info, setUser_info] = useState([])
  const [products, setProducts] = useState([])
  const [getComment, setgetComment] = useState([])
  const { id } = useParams();
  const { user } = useSelector((state) => state.user)
  const user_id = user.id
  console.log(id);

  useEffect(() => {
    getProduct();
  }, [])


  const [comment, setComment] = useState('')
  const [review, setReview] = useState(0)

  const ratingChanged = (review) => {
    setReview(review)
  };

  const getProduct = () => {
    Axios.get(`/products/${id}`,)
      .then((res) => {
        setProducts(res.data.data.products);
        setgetComment(res.data.data.review);
        setUser_info(res.data.data.user_i);

      })
      .catch(err => { throw err });
  }

  const reviews = () => {
    Axios.post('/set-review', {
      user_id,
      id,
      review,
      comment
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  return (
    <>
      <Card style={{ alignItems: 'center', padding: '50px' }}>

          return (
            <Card key={product.prodid} style={{ padding: '50px', align: 'center' }}>
              <Card.Title><h1>{product.product}</h1></Card.Title>
              <Card.Img style={{ width: '500px', height: '500px' }} variant="top" src={'http://localhost:5000/images/' + product.img} />
              <Card.Body>
                <Card.Subtitle>{product.description}</Card.Subtitle>
              </Card.Body>
              <div className="input01" style={{ align: 'center' }}>
                <ReactStars
                  count={5}
                  onChange={ratingChanged}
                  size={30}
                  activeColor="#78c2ad" />
                <input name='comment' className='input0' type='text' placeholder='Review' onChange={(e) => setComment(e.target.value)} /><br />
              </div>
              <Button type='button' variant='outline-secondary' onClick={reviews}>Submit</Button>
            </Card>)

      </Card>
      <Card>
        {getComment.map((comments) => {
          console.log(getComment);
          return (
            <Card key={comments.id} style={{ padding: '50px', align: 'center' }}>
              {user_info.map((data) => {
                return (
                  <Card.Body key={data.id}>
                    <Card.Text>user id: {comments.user_id} {data.name} {data.surname}<br />
                      review: {comments.review} <br />
                    </Card.Text>
                    <Card.Text>comment: <br />
                      {comments.comment}
                    </Card.Text>
                  </Card.Body>
                )
              })}
            </Card>
          )
        })}
      </Card>
    </ >
  )
}

export default ProductDetails
