import React, { useState } from 'react'
import { useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Header from './Header'
import Axios from 'axios'

const ChangePassword = () => {
    const [email, setEmail] = useState('')
    const [error, setError] = useState('')
    const [user, setUser] = useState('')

    const findUser = () => {
        Axios.post('/change-password', {
            email,
        })
            .then(response => {
                setUser(response.data.data.user);

            })
            .catch(err => {
                const response = err.response.data.validationError[0];
                console.log(err.response.data.validationError[0]);
                if (response) {
                    setError(`${response.message}`)
                }
            });
    };

    return (
        <Header title="Change your password">

            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
                <Form>
                <div className='text-left font-weight-bold text-danger'>
                        {error}
                    </div>
                    <div className="form-group" style={{ border: "1px solid #78c2ad", padding: "20px" }}>
                        <div className="inputs">

                            <h3>Username:</h3>
                            <input type="email" name="email" onChange={(e) => { setEmail(e.target.value) }} /><br />
                            <Button variant='outline-secondary' type="button" onClick={() => findUser()} >Submit</Button>
                            <br />
                            <Link to="/login"> Log in </Link>
                        </div>
                    </div>
                </Form>
            </div>
        </Header>
    )
}

export default ChangePassword
