import ReactStars from "react-rating-stars-component";
import ReactPaginate from 'react-paginate'
import React, { useEffect, useState } from 'react'
import { CardGroup, Row, Pagination } from 'react-bootstrap';
import Header from './Header';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useSelector } from 'react-redux';
import Product from "./Product";

const Products = () => {
   const [products, setProducts] = useState([])
   const [pageNumber, setPageNumber] = useState(0)
   const usersPerPage = 10
   const visitedPages = pageNumber * usersPerPage


   const displayUser = users.slice(visitedPages, visitedPages + usersPerPage).map((user) => {
     return (
       <tr key={user.id} >
         <td>{user.id}</td>
         <td>{user.name}</td>
         <td>{user.surname}</td>
         <td>{new Date().getFullYear() - new Date(user.age).getFullYear()}</td>
         <td>{user.email}</td>
         <td>{user.isAdmin}</td>
       </tr>)

   })


  useEffect(() => {
    Axios.get('/products')
      .then((res) => {
        setProducts(res.data.data.products)
      });
  }, [])

  return (
    <Header title='Products'>
      <CardGroup style={{ display: 'flex', flexDirection: 'row' }}>
        <Row >
          {products.map((product) => (
            <Product
              key={product.prodid}
              product={product}
            />
          ))}
        </Row>
      </CardGroup>
    </Header>
  );
}

export default Products
