import ReactStars from "react-rating-stars-component";
import React, { useEffect, useState } from 'react'
import { Button, Card, CardGroup, CardImg, Container, Form, FormControl, FormGroup, FormLabel, Row } from 'react-bootstrap';
import Header from './Header';
import './Products.css';

import './components/Topbar/bootstrap.min.css';
import Axios from "axios";

const Products = () => {

  const [comment, setComment] = useState('')
  const [review, setReview] = useState(0)
  const [productList, setProductList] = useState('')

  const ratingChanged = (review) => {
    setReview(review)
  };

  const reviews = () => {
    Axios.post('/set-review', {
      review,
      comment
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  useEffect(() => {
    Axios.get('/products')
      .then((res) => {
        setProductList(res.data.data.productList)
      });
  }, [])

  return (
    <Header title='Products'>
<CardGroup style={{display: 'flex', flexDirection: 'row'}}>
{productList.localeCompare((product)=>{
return <Card style={{ width: '18rem' }} key={product.prodid}>
<Card.Img variant="top"></Card.Img>
<Card.Body>
  <Card.Title>Card Title</Card.Title>
  <Card.Text>
    Some quick example text to build on the card title and make up the bulk of
    the card's content.
  </Card.Text>
  <Button variant="primary">View</Button>
</Card.Body>
</Card>




})}

</CardGroup>
      <div className="input01">

        <ReactStars
          count={5}
          onChange={ratingChanged}
          size={30}
          activeColor="#2045B6" />
        <input name='comment' className='input0' type='text' placeholder='Review' onChange={(e) => setComment(e.target.value)} /><br />

        <Button type='button' onClick={reviews}>Submit</Button> </div>


    </Header>



  );
}

export default Products
