import ReactStars from "react-rating-stars-component";
import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import { Axios } from "axios";





const Products = () => {
  const products = () => {
    Axios.post('/Products', {
      product,
      description,  
      img,
      comment
         })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };


  const [product,setProduct]=useState('')
  const [description,setDescription]=useState('')
  const [img,setImg]=useState('')
  const [comment,setComment]=useState('')

  const ratingChanged = (newRating) => {
    console.log(newRating);
  };

  const [file, setFile] = useState();
  const [fileName, setFileName] = useState("");

  const saveFile = (e) => {
    setFile(e.target.files[0]);
    setFileName(e.target.files[0].name);
  };

  return (
    <Header title='Products'>


      <Card className="text-center">
        <Card.Header><h1>Name</h1></Card.Header>
        <Card.Body>

          <Card.Text style={{ marginBottom: 20 }}>
            Description

            <Card.Img variant="bottom" src="holder.js/100px180" />

          </Card.Text>

          <Button variant="primary" >Prev</Button>
          <Button variant="primary" >Delete</Button>
          <Button variant="primary" >Next</Button>


        </Card.Body>

      </Card>

      <div className="input01"><ReactStars
        count={5}
        onChange={ratingChanged}
        size={30}
        activeColor="#2045B6" />
        <input name='comment' className='input0' type='text' placeholder='review' onChange={(e)=>setComment(e.target.value)}/><br />
        <Button type='button'>Submit</Button> </div>


      <Card className="text-center">
        <Card.Header>
          <h1>Add new pruduct</h1>

        </Card.Header>
        <Card.Body>

          <Card.Text >


            <textarea name='product' className='input0' type='text' placeholder='Products name' required onChange={(e)=>setProduct(e.target.value)} />
            <br/><br/>


            <textarea name='description' className='input' type='text' placeholder='Description' required onChange={(e)=>setDescription(e.target.value)} />

            <br/>

            <input name='img' onChange={saveFile} type='file' style={{ padding: 20 }} ></input>
          </Card.Text>
          <Button type="button" onClick={products}>Add</Button>



        </Card.Body>

      </Card>
    </Header>



  );
}

export default Products
