import React, { useState } from 'react'
import { useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Header from './Header'

const ChangePassword = () => {
const[error,setError]=useState('')

const findUser = () => {
      Axios.post('login', {
      email,
      password,
    })
      .then(response => {
        navigate('/products');
        dispatch(setUser(response.data.data.user))
      })
      .catch(err => {
        console.log(err);
        const response = err.response.data.validationError[0];
        if (response) {
          setError(`${response.field}: ${response.message}`)
        }
      });
  };

    return (
    <Header title="Change your password">
         <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
      <Form>
        <div className="form-group" style={{ border: "1px solid #78c2ad", padding: "20px" }}>
          <div className="inputs">
            <div className='text-left font-weight-bold text-danger'>
              {error}
            </div>
            <h3>Username:</h3>
            <input type="email" name="email" /><br />
            <Button variant='outline-secondary' onClick={findUser} >Login</Button>


            <h3>Password:</h3>
            <input type="password" name="password" /><br /><br />

            <Button variant='outline-secondary' >Login</Button>
            <br /> <br />
            <Link to="/login"> Log in </Link> 

          </div>
        </div>
      </Form>
    </div>
    </Header>
    )
}

export default ChangePassword
