import Axios from 'axios';
import React, { useState } from 'react'
import Header from './Header';
import "./components/Topbar/bootstrap.min.css";
import { useDispatch } from 'react-redux'
import { setUser } from './UserStore'
import { Link, useNavigate } from 'react-router-dom';
import { Button, Form, link } from 'react-bootstrap';

export default function Login() {
  const dispatch = useDispatch()
  const [email, setEmail] = useState('');
  const [password, setPass] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const login = (e) => {

    Axios.post('/login', {
      email,
      password,
    })
      .then(response => {
        navigate('/products');
        dispatch(setUser(response.data.data.user))
      })
      .catch(err => {
        console.log(err);
        const response = err.response.data.validationError[0];
        if (response) {
          setError(`${response.field}: ${response.message}`)
        }
      });
  };
  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
      <Form>
        <div className="form-group" style={{ border: "1px solid #78c2ad", padding: "20px" }}>
          <div className="inputs">
            <div className='text-left font-weight-bold text-danger'>
              {error}
            </div>
            <h3>Username:</h3>
            <input type="email"
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              name="email" /><br />
            <h3>Password:</h3>
            <input type="password" onChange={(e) => { setPass(e.target.value) }} name="password" /><br /><br />

            <Button variant='outline-secondary' onClick={() => login()} >Login</Button>
            <br /> <br />
            <Link to="/Register"> Register </Link>
            <Link to="/change-password"> Forgot Password  </Link>
          </div>
        </div>
      </Form>
    </div>
  );
}
