import React, { useState } from 'react'
import Header from './Header'
import axios from 'axios'

// CSS  
import "./Register.css";


export default function Register() {
  const [name, setNewName] = useState('');
  const [surname, setSurname] = useState('');
  const [age, setNewage] = useState('');
  const [email, setNewuser] = useState('');
  const [password, setNewpass] = useState('');

  const register = () => {
    axios.post('/register', {
      name,
      surname,  
      age,
      email,
      password
         })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };


  return (<Header title='Register'>
    <form className="register-form">
      <div className="home">
        <div className="inputs">

          <h3>Name:</h3>
          <input type="text" onChange={(e) => { setNewName(e.target.value) }} name="name" />

          <h3>Surname:</h3>
          <input type="text" onChange={(e) => { setSurname(e.target.value) }} name="surname" />

          <h3>Age:</h3>
          <input type="date" onChange={(e) => { setNewage(e.target.value) }} name="age" />

          <h3>Email:</h3>
          <input type="email" onChange={(e) => { setNewuser(e.target.value) }} name="email" />

          <h3>Password:</h3>
          <input type="password" name="psw" pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}" title="Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters" />
          <input type="password" onChange={(e) => { setNewpass(e.target.value) }} name="password" pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}" title="Must contain at least one number and one uppercase and lowercase letter, and at least 8 or more characters"/>

          <div className="d-flex justify-between">
            <button type="button" onClick={register} className="button">Register</button>

          </div>
          <a href='/login'>Login</a>
        </div>      
      </div>
     </form>
  </Header>
  )
}
