import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";
import { useNavigate, useParams, Link } from 'react-router-dom';
import ReactStars from 'react-rating-stars-component';

const ProductDetails = () => {
  const [products, setProducts] = useState([])
  const { id } = useParams();
  console.log(id);
  useEffect(() => {
    Axios.get(`/products/`, { params: { id } })
      .then((res) => {
        console.log(params)
        setProducts(res.data.data.products);
        console.log(res.data.data.products);
      })
      .catch(err => { throw err });
  }, [])
  const [comment, setComment] = useState('')
  const [review, setReview] = useState(0)
  const ratingChanged = (review) => {
    setReview(review)
  };

  const reviews = () => {
    Axios.post('/set-review', {
      review,
      comment
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };

  return (
    <Header title='Details'>
      <Card>
        {products.map((product) => {
          return (
            <Card key={product.prodid} style={{ padding: '20px' }}>
              <Card.Img style={{ width: '18rem', height: '18rem' }} variant="top" src={'http://localhost:5000/images/' + product.img} />
              <Card.Body>
                <Card.Title>{product.product}</Card.Title>
              </Card.Body>
              <div className="input01">
                <ReactStars
                  count={5}
                  onChange={ratingChanged}
                  size={30}
                  activeColor="#2045B6" />
                <input name='comment' className='input0' type='text' placeholder='Review' onChange={(e) => setComment(e.target.value)} /><br/>

                <Button type='button' onClick={reviews}>Submit</Button> </div>
            </Card>)
        })}
      </Card>
      <Link to={`/products`}>
        <Button variant="primary">Back</Button>
      </Link  >
    </Header >
  )
}

export default ProductDetails
