import Axios from 'axios';
import React, { useState } from 'react'
import Header from './Header';
import Products from './Products';
import "./Register.css";
import { Navigate,Outlet } from 'react-router-dom';


export default function Login() {
  const [email, setUser] = useState('');
  const [password, setPass] = useState('');



  const login = () => {
    Axios.post('/login', {
      email,
      password,
    })
      .then(response => { if (response.data.accessToken) {
        localStorage.setItem("user", JSON.stringify(response.data));
      }
      return response.data;

      })
      .catch(err => {throw err });
  };



  return (
    <Header title='Login'>
      <form className="register-form">


        <div className="home">

          <div className="inputs">


            <h3>Username:</h3>
            <input type="email" onChange={(e) => { setUser(e.target.value) }} name="email" />
            <h3>Password:</h3>
            <input type="password" onChange={(e) => { setPass(e.target.value) }} name="password" />
            <button type='button' onClick={login} className="button">Login</button>
            <br /> <br />

            <a href="/Register"> Register </a>
          </div>
        </div></form></Header>
  );
}
