import { Dropdown, Navbar, Nav, Container, FormControl, Button, DropdownButton, Table } from 'react-bootstrap';
import '/home/user/test-project/client/src/components/Topbar/bootstrap.min.css'
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux'
import { setUser } from './UserStore.js'
import Products from './Products.jsx'
import { useParams } from 'react-router-dom';
import React, { useEffect, useState } from 'react'
import Axios from 'axios';

const Topbar = () => {
  const { user } = useSelector((state) => state.user)
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const [input, setInput] = useState("")
  const [output, setOutput] = useState([])

  const logo = () => {

    if (Object.keys(user).length > 0) {
      navigate('/products')
    } 
    else { navigate('/') }
  }

  const logout = () => {

    if (Object.keys(user).length > 0) {
      dispatch(setUser([]));
      navigate('/')
    }
    else <></>
  }

  useEffect(() => {
    Axios.get(`/products`)
      .then((response) => {
        setOutput(response.data.data.products)

      });
  }, [input])


  return (
    <Navbar bg="light" expand="lg">
      <Container fluid>
        <Navbar.Brand onClick={logo} style={{ fontWeight: 'bold', fontSize: 25, padding: 10, cursor: "pointer" }} >
          ã€Œğ”¸ğ•”ğ•”ğ•–ğ•¤ğ•¤ğ• ğ•£ğ•šğ•–ğ•¤ã€
        </Navbar.Brand>
        <Nav
          className="me-auto my-2 my-lg-0"
          style={{ maxHeight: '100px', maxWidth: '100%' }}>
          {
            Object.keys(user).length > 0
              ? <>
                {
                  !!user.isAdmin
                    ? <>
                      <DropdownButton variant="outline-secondary" id="dropdown-basic" title="Admin" >

                        <Dropdown.Item>
                          <Link to="/users" style={{ padding: 10 }}>Users</Link>
                        </Dropdown.Item>

                        <Dropdown.Item>

                          < Link to="/reviews" style={{ padding: 10 }}>Reviews</Link>
                        </Dropdown.Item>
                        <Dropdown.Item>
                          <Link to="/add-product" style={{ padding: 10 }}>New Product</Link>
                        </Dropdown.Item>

                      </DropdownButton>
                      <Link to="/products" style={{ padding: 10 }} >Products</Link>
                    </>

                    : <Link to="/products" style={{ padding: 10 }}>Products</Link>

                }
                <Nav>
                  <div>
                    <FormControl
                      style={{ width: "1000px", marginLeft: "10%", display: "flex" }}
                      className="search"
                      type="text"
                      placeholder="Search"
                      aria-label="Search"
                      onChange={e => setInput(e.target.value)}
                    />
                    <div className='dropdown'>
                      {output.map((item) => (
                        <div className='dropdown-row' key={item.prodid}>
                          {item.product}
                        </div>))}
                    </div>
                  </div>
                </Nav>
              </>

              : <></>
          }
        </Nav>
        {Object.keys(user).length > 0
          ? <Nav><Button variant="outline-secondary" onClick={logout} >Logout</Button></Nav>
          : <></>}
      </Container>
    </Navbar>
  )
}

export default Topbar
