import ReactStars from "react-rating-stars-component";
import React, { useEffect, useState } from 'react'
import { Button, Card } from 'react-bootstrap';
import Header from './Header';
import './Products.css';
import './components/Topbar/bootstrap.min.css';
import Axios from "axios";


const Products = () => {


  const [product, setProduct] = useState('')
  const [description, setDescription] = useState('')
  const [img, setImg] = useState('')



  const [comment, setComment] = useState('')


  const [productList, setProductList] = useState([]);

  const AddProducts = () => {
    Axios.get('/Products/new').then((response) => {
      console.log(response);
    });
  }

  const reviews = () => {
    Axios.post('/Products', {
      // id,
      review,
      comment,
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };



  const products = () => {
    Axios.post('/Products', {
      product,
      description,
      img,
    })
      .then(response => {
        console.log(response)
      })
      .catch(err => { throw err });
  };


  const [review, setReview] = useState(0)

  // function StarRating({ count, value,
  //   inactiveColor = '#ddd',
  //   size = 30,
  //   activeColor = "#2045B6", onChange
  // }) {
  //   const stars = Array.from({ length: count }, () => '')

// };

    const ratingChanged= (newRating) => {
      console.log(newRating+1);
    };




  const [file, setFile] = useState("");
  const [fileName, setFileName] = useState("");

  const saveFile = (e) => {
    setFile(e.target.files[0]);
    setFileName(e.target.files[0].name);
  };
  const uploadFile = async (e) => {
    const formData = new FormData();
    formData.append("file", file);
    formData.append("fileName", fileName);

  };

  return (
    <Header title='Products'>
      <Card className="text-center">
        <Card.Header><h1>Name</h1></Card.Header>
        <Card.Body>
         {/* <Row> {
                  products.map(product=>{
                    return<Col md={6} lg={4} sm={12} key={products.id}>
                      <ProductCard product={products}/>
                    </Col>
                  })




                        }</Row> */}
          <Button variant="primary" >Prev</Button>
          <Button variant="primary" >Update</Button>
          <Button variant="primary" >Delete</Button>
          <Button variant="primary" >Next</Button>
        </Card.Body>

      </Card>

      <div className="input01">
        <ReactStars
          count={5}
          onChange={ratingChanged}
        />
        <input name='comment' className='input0' type='text' placeholder='Review' onChange={(e) => setComment(e.target.value)} />

        <br /><Button type='button' onClick={reviews}>Submit</Button> </div>


      <Card className="text-center">
        <Card.Header>
          <Button type="button" size="lg" onClick={AddProducts}>Add new pruduct</Button>
        </Card.Header>
        <Card.Body>

          <Card.Text >

            <input name='product' className='input0' type='text' placeholder='Products name' required onChange={(e) => setProduct(e.target.value)} />
            <br /><br />


            <textarea name='description' className='input' type='text' placeholder='Description' required onChange={(e) => setDescription(e.target.value)} />

            <br />

            <input name='img' type="file" onChange={saveFile} />
            <button onClick={uploadFile}>Upload</button>

          </Card.Text>
          <Button type="button" onClick={products}>Add</Button>



        </Card.Body>

      </Card>
    </Header>



  );
}

export default Products
